import React, { useState, useEffect } from 'react';
import axios from "axios";

const ProductsCat = ({ cart, setCart }) => {
  const [searchQuery, setSearchQuery] = useState("");
  const [notification, setNotification] = useState("");
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [products, setProducts] = useState([]); // حالة لتخزين المنتجات

  // جلب المنتجات من الـ API
  useEffect(() => {
    axios
      .get("http://localhost/api.php")
      .then((response) => {
        console.log("البيانات المستلمة:", response.data);
        setProducts(response.data);
      })
      .catch((error) => {
        console.error("حدث خطأ أثناء جلب البيانات:", error);
      });
  }, []);

  // تصفية المنتجات بناءً على البحث
  const filteredProducts = products.filter(
    (product) =>
      product.nom.toLowerCase().includes(searchQuery.toLowerCase())
  );

  // إضافة منتج إلى العربة
  const handleAddToCart = (product) => {
    const productInCart = cart.find((item) => item.produit_id === product.produit_id);
    let newCart;

    if (productInCart) {
      newCart = cart.map((item) =>
        item.produit_id === product.produit_id
          ? { ...item, quantity: item.quantity + 1 }
          : item
      );
    } else {
      newCart = [...cart, { ...product, quantity: 1 }];
    }

    setCart(newCart);
    setNotification(`${product.nom} تم إضافته إلى السلة.`);
    setTimeout(() => setNotification(""), 3000);
  };

  // فتح تفاصيل المنتج
  const handleProductClick = (product) => {
    setSelectedProduct(product);
  };

  // إغلاق تفاصيل المنتج
  const handleCloseProductDetails = () => {
    setSelectedProduct(null);
  };

  return (
    <div className="product-page">
      <h2>منتجاتنا</h2>
      <input
        type="text"
        placeholder="ابحث عن منتج"
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
      />

      {notification && <div className="notification">{notification}</div>}

      <div className="products-list">
        {filteredProducts.length > 0 ? (
          filteredProducts.map((product) => (
            <div key={product.produit_id} className="product-card">
              <img
                src={`http://localhost/${product.url}`} // تأكد من تحديث رابط الصورة
                alt={product.nom}
                onClick={() => handleProductClick(product)}
              />
              <h3>{product.nom}</h3>
              <p>{product.prix} DH</p>
              <button onClick={() => handleAddToCart(product)}>أضف إلى السلة</button>
            </div>
          ))
        ) : (
          <p>لم يتم العثور على منتجات.</p>
        )}
      </div>

      {selectedProduct && (
        <div className="product-details">
          <button className="close-btn" onClick={handleCloseProductDetails}>إغلاق</button>
          <img src={`http://localhost/${selectedProduct.url}`} alt={selectedProduct.nom} />
          <h3>{selectedProduct.nom}</h3>
          <p>{selectedProduct.description}</p>
          <p>{selectedProduct.prix} DH</p>
          <button onClick={() => handleAddToCart(selectedProduct)}>أضف إلى السلة</button>
        </div>
      )}
    </div>
  );
};

export default ProductsCat;
