import React, { useState } from "react";

// Images for the products
const collierImage = "/assets/Collier Réfléchissant Chat.jpg";
const fontaineImage = "/assets/fontaine_eau_chat.jpg";
const arbreChatImage = "/assets/arbre_chat_deluxe.jpg";
const sacTransportImage = "/assets/sac_transport_chat.jpg";
const litiereImage = "/assets/litiere_autonettoyante.jpg";
const harnaisImage = "/assets/harnais_chat.jpg";

const laserImage = "/assets/laser_chat.jpg";
const sourisImage = "/assets/souris_peluche.jpg";
const balleDistributeurImage = "/assets/balle_distributeur_chat.jpg";
const tunnelImage = "/assets/tunnel_jeu_chat.jpg";
const jouetSuspenduImage = "/assets/jouet_suspendu.jpg";
const cerceauImage = "/assets/cerceau_chat.jpg";

const shampooingImage = "/assets/shampooing_hydratant_chat.jpg";
const sprayImage = "/assets/spray_anti_stress.jpg";
const lingettesImage = "/assets/lingettes_chat.jpg";
const peigneImage = "/assets/peigne_chat.jpg";
const coupeGriffesImage = "/assets/coupe_griffes_chat.jpg";
const vitaminesImage = "/assets/vitamines_chat.jpg";

const croquettesImage = "/assets/croquettes_hypo_chat.jpg";
const pateImage = "/assets/patee_poulet.jpg";
const friandisesImage = "/assets/friandises_thon.jpg";
const mousseImage = "/assets/mousse_saumon.jpg";
const croquettesJuniorImage = "/assets/croquettes_junior.jpg";
const mixSaveursImage = "/assets/mix_saveurs_chat.jpg";

// ... autres images des produits

const Products = () => {
  const [searchQuery, setSearchQuery] = useState("");
  const [notification, setNotification] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("");
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [quantity, setQuantity] = useState(1);
  const [cart, setCart] = useState([]);
  const [isCartVisible, setIsCartVisible] = useState(false); // État pour afficher ou masquer le panier

  // Product list
  const products = [
    // Accessories
    { id: 1, name: "Collier Réfléchissant Chat", category: "Accessoires", price: "15.99€", description: "Collier ajustable et réfléchissant pour la sécurité du chat", image: collierImage },
    { id: 2, name: "Fontaine à Eau", category: "Accessoires", price: "39.99€", description: "Fontaine à eau automatique pour chats", image: fontaineImage },
    { id: 3, name: "Arbre à Chat Deluxe", category: "Accessoires", price: "119.99€", description: "Grand arbre à chat avec griffoir et niches", image: arbreChatImage },
    { id: 4, name: "Sac de Transport Chat", category: "Accessoires", price: "34.99€", description: "Sac de transport confortable pour voyages", image: sacTransportImage },
    { id: 5, name: "Litière Autonettoyante", category: "Accessoires", price: "199.99€", description: "Litière pour chat autonettoyante", image: litiereImage },
    { id: 6, name: "Harnais pour Chat", category: "Accessoires", price: "17.99€", description: "Harnais réglable avec laisse", image: harnaisImage },

    // Toys
    { id: 7, name: "Laser pour Chat", category: "Jeux", price: "25.99€", description: "Jouet interactif avec laser automatique", image: laserImage },
    { id: 8, name: "Souris en Peluche", category: "Jeux", price: "7.99€", description: "Jouet en forme de souris avec catnip", image: sourisImage },
    { id: 9, name: "Balle Distributeur de Nourriture", category: "Jeux", price: "14.99€", description: "Balle interactive pour distribuer des friandises", image: balleDistributeurImage },
    { id: 10, name: "Tunnel de Jeu XXL", category: "Jeux", price: "29.99€", description: "Tunnel pour chat avec plusieurs entrées", image: tunnelImage },
    { id: 11, name: "Jouet Suspendu", category: "Jeux", price: "12.99€", description: "Jouet à suspendre pour stimuler le jeu", image: jouetSuspenduImage },
    { id: 12, name: "Cerceau de Jeu", category: "Jeux", price: "18.99€", description: "Cerceau d'agilité pour chats", image: cerceauImage },

    // Care
    { id: 13, name: "Shampooing Hydratant Chat", category: "Soins", price: "13.99€", description: "Shampooing pour poils secs et sensibles", image: shampooingImage },
    { id: 14, name: "Spray Anti-Stress", category: "Soins", price: "19.99€", description: "Spray aux phéromones pour calmer les chats", image: sprayImage },
    { id: 15, name: "Lingettes Nettoyantes", category: "Soins", price: "8.99€", description: "Lingettes pour nettoyer le pelage des chats", image: lingettesImage },
    { id: 16, name: "Peigne Anti-Noeuds", category: "Soins", price: "10.99€", description: "Peigne pour démêler les poils", image: peigneImage },
    { id: 17, name: "Coupe-Griffes", category: "Soins", price: "6.99€", description: "Coupe-griffes avec sécurité", image: coupeGriffesImage },
    { id: 18, name: "Vitamines pour Chat", category: "Soins", price: "14.99€", description: "Supplément alimentaire pour la santé du chat", image: vitaminesImage },

    // Food
    { id: 19, name: "Croquettes Hypoallergéniques", category: "Nourriture", price: "29.99€", description: "Croquettes pour chats sensibles", image: croquettesImage },
    { id: 20, name: "Pâtée au Poulet", category: "Nourriture", price: "2.99€", description: "Pâtée pour chat goût poulet", image: pateImage },
    { id: 21, name: "Friandises au Thon", category: "Nourriture", price: "6.49€", description: "Friandises pour chat riches en protéines", image: friandisesImage },
    { id: 22, name: "Mousse de Saumon", category: "Nourriture", price: "3.49€", description: "Nourriture humide pour chat au saumon", image: mousseImage },
    { id: 23, name: "Croquettes Junior", category: "Nourriture", price: "22.99€", description: "Croquettes pour chatons en croissance", image: croquettesJuniorImage },
    { id: 24, name: "Mix de Saveurs", category: "Nourriture", price: "15.99€", description: "Assortiment de pâtées pour chat", image: mixSaveursImage }
  ];


  // Filtrer les produits en fonction de la recherche et de la catégorie sélectionnée
  const filteredProducts = products.filter(
    (product) =>
      (product.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
        product.category.toLowerCase().includes(searchQuery.toLowerCase())) &&
      (selectedCategory ? product.category === selectedCategory : true)
  );

  // Ajouter un produit au panier
  const handleAddToCart = (productName, price) => {
    setNotification(`${productName} a été ajouté au panier.`);
    const newCart = [...cart, { productName, price, quantity }];
    setCart(newCart);
    setTimeout(() => setNotification(""), 3000); // Supprimer la notification après 3 secondes
  };

  // Changer la catégorie
  const handleCategoryChange = (category) => {
    setSelectedCategory(category);
  };

  // Sélectionner un produit pour afficher ses détails
  const handleProductClick = (product) => {
    setSelectedProduct(product);
  };

  // Changer la quantité du produit
  const handleQuantityChange = (event) => {
    setQuantity(event.target.value);
  };

  // Fermer la fenêtre de détails du produit
  const handleCloseProduct = () => {
    setSelectedProduct(null);
  };

  // Retirer un produit du panier
  const handleRemoveFromCart = (index) => {
    const updatedCart = [...cart];
    updatedCart.splice(index, 1);
    setCart(updatedCart);
  };

  // Calculer le prix total du panier
  const totalPrice = cart.reduce((total, item) => total + parseFloat(item.price.replace("€", "")) * item.quantity, 0).toFixed(2);

  // Afficher ou masquer le panier lorsque l'on clique sur la flèche
  const toggleCartVisibility = () => {
    setIsCartVisible(!isCartVisible);
  };

  return (
    <div className="container">
      {/* Barre de recherche */}
      <input
        type="text"
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
        placeholder="Rechercher un produit..."
      />

      {/* Notification */}
      {notification && <div className="notification">{notification}</div>}

      {/* Boutons de sélection de catégorie */}
      <div>
        <button onClick={() => handleCategoryChange("")}>Tous</button>
        <button onClick={() => handleCategoryChange("Accessoires")}>Accessoires</button>
        <button onClick={() => handleCategoryChange("Jeux")}>Jeux</button>
        <button onClick={() => handleCategoryChange("Soins")}>Soins</button>
        <button onClick={() => handleCategoryChange("Nourriture")}>Nourriture</button>
      </div>

      {/* Liste des produits */}
      <div className="products-list">
        {filteredProducts.map((product) => (
          <div key={product.id} className="product-card" onClick={() => handleProductClick(product)}>
            <img src={product.image} alt={product.name} />
            <h3>{product.name}</h3>
            <p>{product.price}</p>
            <button onClick={() => handleAddToCart(product.name, product.price)}>Ajouter au Panier</button>
          </div>
        ))}
      </div>

      {/* Détails du produit */}
      {selectedProduct && (
        <div className="product-modal">
          <div className="modal-content">
            <button className="close" onClick={handleCloseProduct}>×</button>
            <img className="product-image" src={selectedProduct.image} alt={selectedProduct.name} />
            <div className="modal-details">
              <h2>{selectedProduct.name}</h2>
              <p>{selectedProduct.description}</p>
              <p className="price">{selectedProduct.price}</p>
              <div className="quantity">
                <input type="number" value={quantity} onChange={handleQuantityChange} min="1" max="10" />
              </div>
              <button onClick={() => handleAddToCart(selectedProduct.name, selectedProduct.price)}>
                Ajouter au Panier
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Panier fixe */}
      <div className={`cart ${isCartVisible ? "show" : ""}`}>
        <h2>Mon Panier</h2>
        {cart.length === 0 && <p>Le panier est vide</p>}
        {cart.map((item, index) => (
          <div key={index} className="cart-item">
            <p>{item.productName} - {item.price} x{item.quantity}</p>
            <button onClick={() => handleRemoveFromCart(index)}>Supprimer</button>
          </div>
        ))}
        <div className="total-price">Total: {totalPrice} €</div>
        <button className="checkout-button">Passer à la caisse</button>
      </div>

      {/* Bouton de flèche pour afficher/masquer le panier */}
      <button className="arrow-button" onClick={toggleCartVisibility}>
        {isCartVisible ? "▲" : "▼"} {/* Flèche vers le haut ou vers le bas */}
      </button>
    </div>
  );
};

export default Products;
