import React, { useState, useEffect } from 'react';
import axios from "axios";

const ProductsCat = ({ cart, setCart }) => {
  const [searchQuery, setSearchQuery] = useState("");  // لحفظ نص البحث
  const [notification, setNotification] = useState(""); // لإظهار إشعار بعد إضافة منتج إلى السلة
  const [products, setProducts] = useState([]);  // لحفظ المنتجات المسترجعة من الـ API

  // جلب المنتجات من الـ API عند تحميل المكون
  useEffect(() => {
    axios
      .get("http://localhost/api.php")
      .then((response) => {
        console.log("Produits récupérés : ", response.data);
        const uniqueProducts = Array.from(new Set(response.data.map(a => a.produit_id)))
          .map(id => {
            return response.data.find(a => a.produit_id === id)
          });
        setProducts(uniqueProducts);
      })
      .catch((error) => {
        console.error("Erreur lors de la récupération des produits:", error.message);
      });
  }, []);
  

  // تصفية المنتجات بناءً على نص البحث فقط
  const filteredProducts = products.filter(
    (product) =>
      product.nom.toLowerCase().includes(searchQuery.toLowerCase())  // فلترة المنتجات حسب نص البحث
  );

  // إضافة منتج إلى السلة
  const handleAddToCart = (product) => {
    const productInCart = cart.find((item) => item.produit_id === product.produit_id);
    let newCart;

    if (productInCart) {
      newCart = cart.map((item) =>
        item.produit_id === product.produit_id
          ? { ...item, quantity: Math.min(item.quantity + 1, 10) } // Limiter la quantité à 10
          : item
      );
    }
    else {
      newCart = [...cart, { ...product, quantity: 1 }];
    }

    setCart(newCart);
    setNotification(`${product.nom} ajouté au panier. Prix : ${product.prix}`);
    setTimeout(() => setNotification(""), 3000); // إخفاء الإشعار بعد 3 ثواني
  };

  return (
    <div className="product-page">
      <h2>Nos Produits</h2>
      
      {/* حقل البحث */}
      <input
        type="text"
        placeholder="Rechercher un produit"
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}  // تحديث نص البحث
      />

      {/* عرض الإشعار إذا كان موجودًا */}
      {notification && <div className="notification">{notification}</div>}

      {/* عرض قائمة المنتجات بعد التصفية */}
      <div className="products-list">
        {filteredProducts.length > 0 ? (
          filteredProducts.map((product) => (
            <div key={product.produit_id} className="product-card">
              <img
                src={`http://localhost/images/${product.url}`} // استخدام URL الصورة المسترجعة من الـ API
                alt={product.nom}
              />
              <h3>{product.nom}</h3>
              <p>{product.prix} dh</p>
              <button onClick={() => handleAddToCart(product)}>Ajouter au panier</button>
            </div>
          ))
        ) : (
          <p>Aucun produit trouvé.</p>  // في حال عدم وجود منتجات تطابق نص البحث
        )}
      </div>
    </div>
  );
};

export default ProductsCat;
