import React, { useState, useEffect } from 'react';
import axios from "axios";

const ProductsDog = ({ cart, setCart }) => {
  const [searchQuery, setSearchQuery] = useState("");  // Pour conserver la recherche
  const [notification, setNotification] = useState(""); // Pour afficher les notifications
  const [products, setProducts] = useState([]);  // Pour stocker les produits récupérés

  // Récupérer les produits de chien depuis l'API
  useEffect(() => {
    axios
      .get("http://localhost/api_dog.php")  // Changez cette URL en fonction de votre API pour les produits de chien
      .then((response) => {
        console.log("Produits de chien récupérés : ", response.data);
        const uniqueProducts = Array.from(new Set(response.data.map(a => a.produit_id)))
          .map(id => {
            return response.data.find(a => a.produit_id === id)
          });
        setProducts(uniqueProducts);
      })
      .catch((error) => {
        console.error("Erreur lors de la récupération des produits:", error.message);
      });
  }, []);

  // Filtrer les produits selon la recherche
  const filteredProducts = products.filter(
    (product) =>
      product.nom.toLowerCase().includes(searchQuery.toLowerCase())  // Filtrer par nom ou catégorie
  );

  // Ajouter un produit au panier
  const handleAddToCart = (product) => {
    const productInCart = cart.find((item) => item.produit_id === product.produit_id);
    let newCart;

    if (productInCart) {
      newCart = cart.map((item) =>
        item.produit_id === product.produit_id
          ? { ...item, quantity: Math.min(item.quantity + 1, 10) } // Limiter la quantité à 10
          : item
      );
    } else {
      newCart = [...cart, { ...product, quantity: 1 }];
    }

    setCart(newCart);
    setNotification(`${product.nom} ajouté au panier. Prix : ${product.prix}`); // Notification après ajout
    setTimeout(() => setNotification(""), 3000);  // Cacher la notification après 3 secondes
  };

  return (
    <div className="product-page">
      <h2>Nos Produits pour Chiens</h2>
      
      {/* Champ de recherche */}
      <input
        type="text"
        placeholder="Rechercher un produit"
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}  // Mise à jour de la recherche
      />

      {/* Affichage de la notification */}
      {notification && <div className="notification">{notification}</div>}

      {/* Affichage des produits filtrés */}
      <div className="products-list">
        {filteredProducts.length > 0 ? (
          filteredProducts.map((product) => (
            <div key={product.produit_id} className="product-card">
              <img
                src={product.url} // Utiliser l'URL de l'image du produit
                alt={product.nom}
              />
              <h3>{product.nom}</h3>
              <p>{product.prix} dh</p>
              <button onClick={() => handleAddToCart(product)}>Ajouter au panier</button>
            </div>
          ))
        ) : (
          <p>Aucun produit trouvé.</p>  // Message quand aucun produit ne correspond à la recherche
        )}
      </div>
    </div>
  );
};

export default ProductsDog;
