import React, { useState, useEffect } from "react";
import axios from "axios";

const CatProda = ({ cart, setCart }) => {
  const [searchQuery, setSearchQuery] = useState("");
  const [notification, setNotification] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("");
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [productsChat, setProductsChat] = useState([]);
  const [productsChien, setProductsChien] = useState([]);

  useEffect(() => {
    // Fetch products from API
    axios
      .get("http://localhost/API.php")
      .then((response) => {
        const { chat, chien } = response.data;
        setProductsChat(chat); // Set chat products
        setProductsChien(chien); // Set chien products
      })
      .catch((error) => {
        console.error("There was an error fetching the products!", error);
      });
  }, []); // Run once when the component mounts

  const handleCategoryChange = (category) => {
    setSelectedCategory(category);
  };

  const handleAddToCart = (product) => {
    const productInCart = cart.find((item) => item.id === product.id);
    let newCart;

    if (productInCart) {
      newCart = cart.map((item) =>
        item.id === product.id
          ? { ...item, quantity: item.quantity + 1 }
          : item
      );
    } else {
      newCart = [...cart, { ...product, quantity: 1 }];
    }

    setCart(newCart);
    setNotification(`${product.name} ajouté au panier.`);
    setTimeout(() => setNotification(""), 3000);
  };

  const handleProductClick = (product) => {
    setSelectedProduct(product);
  };

  const handleCloseProductDetails = () => {
    setSelectedProduct(null);
  };

  // Filter products by search query and selected category
  const filteredProducts = [...productsChat, ...productsChien].filter(
    (product) =>
      (product.nom.toLowerCase().includes(searchQuery.toLowerCase()) ||
        product.categorie?.toLowerCase().includes(searchQuery.toLowerCase())) &&
      (selectedCategory ? product.categorie === selectedCategory : true)
  );

  return (
    <div className="product-page">
      <h2>Nos Produits</h2>
      <input
        type="text"
        placeholder="Rechercher un produit"
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
      />
      <div>
        <button onClick={() => handleCategoryChange("Accessoires")}>Accessoires</button>
        <button onClick={() => handleCategoryChange("Jeux")}>Jeux</button>
        <button onClick={() => handleCategoryChange("Soins")}>Soins</button>
        <button onClick={() => handleCategoryChange("Nourriture")}>Nourriture</button>
        <button onClick={() => setSelectedCategory("")}>Tous</button>
      </div>

      {notification && <div className="notification">{notification}</div>}

      <div className="products-list">
        {filteredProducts.map((product) => (
          <div key={product.produit_id} className="product-card">
            <img
              src={product.image}
              alt={product.nom}
              onClick={() => handleProductClick(product)}
            />
            <h3>{product.nom}</h3>
            <p>{product.prix}</p>
            <button onClick={() => handleAddToCart(product)}>Ajouter au panier</button>
          </div>
        ))}
      </div>

      {selectedProduct && (
        <div className="product-details">
          <button className="close-btn" onClick={handleCloseProductDetails}>X</button>
          <img src={selectedProduct.image} alt={selectedProduct.nom} />
          <h3>{selectedProduct.nom}</h3>
          <p>{selectedProduct.description}</p>
          <p>{selectedProduct.prix}</p>
          <button onClick={() => handleAddToCart(selectedProduct)}>Ajouter au panier</button>
        </div>
      )}
    </div>
  );
};

export default CatProda;
