import React, { useState } from "react";

// Product images

const ProdutsDog = ({ cart, setCart }) => {
  const [searchQuery, setSearchQuery] = useState("");
  const [notification, setNotification] = useState("");
  const [selectedProduct, setSelectedProduct] = useState(null); // Track selected product for details view
  const [products, setProducts] = useState([]);



  
  // Filter products by search query and selected category
  const filteredProducts = products.filter(
    (product) =>
      (product.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
        product.category.toLowerCase().includes(searchQuery.toLowerCase()))
  );

  // Handle category change


  // Handle adding product to cart
  const handleAddToCart = (product) => {
    const productInCart = cart.find((item) => item.id === product.id);
    let newCart;

    if (productInCart) {
      newCart = cart.map((item) =>
        item.id === product.id
          ? { ...item, quantity: item.quantity + 1 }
          : item
      );
    } else {
      newCart = [...cart, { ...product, quantity: 1 }];
    }

    setCart(newCart);
    setNotification(`${product.name} ajouté au panier.`);
    setTimeout(() => setNotification(""), 3000);
  };

  // Handle product selection for details view
  const handleProductClick = (product) => {
    setSelectedProduct(product); // Show the selected product in a detailed view
  };

  // Handle closing the product detail view
  const handleCloseProductDetails = () => {
    setSelectedProduct(null);
  };

  return (
    <div className="product-page">
      <h2>Nos Produits</h2>
      <input
        type="text"
        placeholder="Rechercher un produit"
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
      />

      {notification && <div className="notification">{notification}</div>}

      <div className="products-list">
        {filteredProducts.map((product) => (
          <div key={product.id} className="product-card">
            <img
              src={product.image}
              alt={product.name}
              onClick={() => handleProductClick(product)} // View product details on click
            />
            <h3>{product.name}</h3>
            <p>{product.price}</p>
            <button onClick={() => handleAddToCart(product)}>Ajouter au panier</button>
          </div>
        ))}
      </div>

      {selectedProduct && (
        <div className="product-details">
          <button className="close-btn" onClick={handleCloseProductDetails}>X</button>
          <img src={selectedProduct.image} alt={selectedProduct.name} />
          <h3>{selectedProduct.name}</h3>
          <p>{selectedProduct.description}</p>
          <p>{selectedProduct.price}</p>
          <button onClick={() => handleAddToCart(selectedProduct)}>Ajouter au panier</button>
        </div>
      )}
    </div>
  );
};

export default ProdutsDog;
