import React, { useState, useEffect } from 'react';
import axios from "axios";

const CatProducts = ({ cart, setCart }) => {
  const [searchQuery, setSearchQuery] = useState("");
  const [notification, setNotification] = useState("");
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [products, setProducts] = useState([]); // État pour les produits récupérés depuis l'API

  // Effectuer la requête API pour récupérer les produits
  useEffect(() => {
    axios
      .get("http://localhost/api.php")
      .then((response) => {
        console.log("Produits récupérés : ", response.data); // تأكد من أن البيانات تحتوي على categorie_id
        setProducts(response.data); // استخدم البيانات كما هي
      })
      .catch((error) => {
        console.error("Erreur lors de la récupération des produits:", error.message);
      });
  }, []);
  

  // Filtrer les produits en fonction de la recherche et de la catégorie
  const filteredProducts = products.filter(
    (product) =>
      product.nom.toLowerCase().includes(searchQuery.toLowerCase())
  );
  

  // Gérer le changement de catégorieconst handleCategoryChange = (categoryId) => {


  // Gérer l'ajout d'un produit au panier
  const handleAddToCart = (product) => {
    const productInCart = cart.find((item) => item.produit_id === product.produit_id); // Vérifier si le produit est déjà dans le panier
    let newCart;

    if (productInCart) {
      newCart = cart.map((item) =>
        item.produit_id === product.produit_id
          ? { ...item, quantity: item.quantity + 1 }  // Si produit existe, augmenter la quantité
          : item
      );
    } else {
      newCart = [...cart, { ...product, quantity: 1 }];  // Ajouter un nouveau produit avec quantité 1
    }

    setCart(newCart);  // Mettre à jour le panier
    setNotification(`${product.nom} ajouté au panier.`); // Afficher une notification
    setTimeout(() => setNotification(""), 3000);  // Cacher la notification après 3 secondes
  };

  // Gérer la sélection d'un produit pour la vue détaillée
  const handleProductClick = (product) => {
    setSelectedProduct(product); // Afficher le produit sélectionné dans la vue détaillée
  };

  // Gérer la fermeture de la vue détaillée du produit
  const handleCloseProductDetails = () => {
    setSelectedProduct(null);
  };

  return (
    <div className="product-page">
      <h2>Nos Produits</h2>
      <input
        type="text"
        placeholder="Rechercher un produit"
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
      />


      {notification && <div className="notification">{notification}</div>}

      <div className="products-list">
        {filteredProducts.length > 0 ? (
          filteredProducts.map((product) => (
            <div key={product.produit_id} className="product-card">
              <img
                src={product.url} // Utiliser l'URL de l'image récupérée depuis l'API
                alt={product.nom}
                onClick={() => handleProductClick(product)} // Voir les détails du produit au clic
              />
              <h3>{product.nom}</h3>
              <p>{product.prix} dh</p>
              <button onClick={() => handleAddToCart(product)}>Ajouter au panier</button>
            </div>
          ))
        ) : (
          <p>Aucun produit trouvé.</p>
        )}
      </div>

      {selectedProduct && (
        <div className="product-details">
          <button className="close-btn" onClick={handleCloseProductDetails}>X</button>
          <img src={selectedProduct.url} alt={selectedProduct.nom} />
          <h3>{selectedProduct.nom}</h3>
          <p>{selectedProduct.description}</p>
          <p>{selectedProduct.prix} dh</p>
          <button onClick={() => handleAddToCart(selectedProduct)}>Ajouter au panier</button>
        </div>
      )}
    </div>
  );
};

export default CatProducts;
