import React, { useState } from "react";

// Images pour les produits
const dogFoodImage = "/assets/dog_food.png";
const catFoodImage = "/assets/cat_food.png";
const petToyImage = "/assets/pet_toy.png";
const ballDog = "/assets/Ball.png";
const TugDog = "/assets/Tug.png";
const Jouet_Burger = "/assets/Jouet-Burger.png";

const Products = () => {
  const [searchQuery, setSearchQuery] = useState(""); // Gérer la recherche
  const [notification, setNotification] = useState(""); // Gérer la notification
  const [selectedCategory, setSelectedCategory] = useState(""); // Gérer la catégorie sélectionnée

  // Liste des produits
  const products = [
    {
      id: 1,
      name: "Croquettes pour chiens",
      category: "Jouets",
      price: "9€",
      image: ballDog ,
    },
    {
      id: 2,
      name: "Tug lapin avec pouic intégré Tug E Nuff",
      category: "Jouets",
      price: "9€",
      image: TugDog ,
    },
    {
      id: 3,
      name: "Croquettes pour chiens",
      category: "Jouets",
      price: "9€",
      image: Jouet_Burger ,
    },
    {
      id: 4,
      name: "Croquettes pour chiens",
      category: "Jouets",
      price: "9€",
      image: ballDog ,
    },
    {
      id: 5,
      name: "Croquettes pour chiens",
      category: "Jouets",
      price: "9€",
      image: ballDog ,
    },
    {
      id: 6,
      name: "Croquettes pour chiens",
      category: "Jouets",
      price: "9€",
      image: ballDog ,
    },
    {
      id: 2,
      name: "Croquettes pour chats",
      category: "Alimentation",
      price: "20€",
      image: catFoodImage,
    },
    {
      id: 3,
      name: "Jouet pour chien",
      category: "Accessoires",
      price: "10€",
      image: petToyImage,
    },
    {
      id: 4,
      name: "Croquettes pour chats",
      category: "Alimentation",
      price: "20€",
      image: catFoodImage,
    },
    {
      id: 5,
      name: "Croquettes pour chiens",
      category: "Alimentation",
      price: "25€",
      image: dogFoodImage,
    },
    {
      id: 6,
      name: "Croquettes pour chiens",
      category: "Alimentation",
      price: "25€",
      image: dogFoodImage,
    },
    {
      id: 7,
      name: "Croquettes pour chats",
      category: "Alimentation",
      price: "20€",
      image: catFoodImage,
    },
    {
      id: 8,
      name: "Jouet pour chien",
      category: "Accessoires",
      price: "10€",
      image: petToyImage,
    },
  ];

  // Filtrage des produits en fonction de la recherche et de la catégorie
  const filteredProducts = products.filter(
    (product) =>
      (product.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
        product.category.toLowerCase().includes(searchQuery.toLowerCase())) &&
      (selectedCategory ? product.category === selectedCategory : true)
  );

  // Fonction pour gérer l'ajout au panier
  const handleAddToCart = (productName) => {
    setNotification(`${productName} a été ajouté au panier.`);
    setTimeout(() => setNotification(""), 3000); // Supprimer la notification après 3 secondes
  };

  // Fonction pour changer la catégorie filtrée
  const handleCategoryChange = (category) => {
    setSelectedCategory(category);
  };

  return (
    <div className="container">
      {/* Affichage de la notification */}
      {notification && (
        <div className="notification">
          <p>{notification}</p>
        </div>
      )}

      <h1>Produits pour Animaux</h1>

      {/* Champ de recherche */}
      <input
        type="text"
        placeholder="Rechercher un produit..."
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)} // Met à jour la recherche
      />

      {/* Boutons pour filtrer les catégories */}
      <div className="category-buttons">
        <button onClick={() => handleCategoryChange("Alimentation")}>Nourriture</button>
        <button onClick={() => handleCategoryChange("Accessoires")}>Accessoires</button>
        <button onClick={() => handleCategoryChange("Jouets")}>Jouets</button>
        <button onClick={() => handleCategoryChange("")}>Tous les produits</button>
      </div>

      {/* Liste des produits sous forme de cartes */}
      <div className="pwwwroducts-list">
        {filteredProducts.length > 0 ? (
          filteredProducts.map((product) => (
            <div key={product.id} className="product-card">
              <img
                src={product.image}
                alt={product.name}
                className="product-image"
              />
              <h3>{product.name}</h3>
              <p>{product.category}</p>
              <p>{product.price}</p>
              <button onClick={() => handleAddToCart(product.name)}>Acheter</button>
            </div>
          ))
        ) : (
          <p>Aucun produit trouvé</p>
        )}
      </div>
    </div>
  );
};

export default Products;
